initScript:
if (form.form.sap_no.value == null || form.form.sap_no.value == '') {
    form.form.sap_no.value = UI.user.login;
    form.form.fullname.value = UI.user.name;
    form.form.email.value = UI.user.email;
    form.form.designation.value = UI.user.title;
    form.form.division.value = UI.user.division;
} else {
    form.form.manager_sap_no.value = UI.user.login;
    form.form.manager_name.value = UI.user.name;
    form.form.manager_email.value = UI.user.email;
    form.form.manager_designation.value = UI.user.title;
    form.form.to.value = UI.user.name;
}
