initScript:
if (is.empty(form.form.sap_no.value)) {
    form.form.sap_no.value = UI.user.login;
    form.form.full_name.value = UI.user.name;
    form.form.email.value = UI.user.email;
    form.form.designation.value = UI.user.properties.designation;
    form.form.business.value = UI.user.properties.businesss;
    HTTP.get('/data/sap_employee_manager?filter=' + UI.user.login).then(function(data) {
        form.form.manager_sap_no.value = data[0].sap_no;
        form.form.manager_name.value = data[0].name;
        form.form.manager_email.value = data[0].email;
        form.form.manager_designation.value = data[0].designation;
    });
}
